import React from 'react'

const AuthContext = React.createContext();

class AuthContextProvider extends React.Component{
	state = {
		// Sign in calls are not used yet
		// Until it is implemented, use "token: null" to view loginStack
		// and "token: 'anything'" to view the homepage
		token: null,
	}
	
	signIn = () => {
		console.log("Signing in!")
		this.token = 'fake-token';
	}

	signOut = () => {
		console.log("Signing out...")
		this.token = null;
	}

	isAuthenticated = () => {
		console.log("TOKEN:", this.state.token)
		console.log("Bool:", this.state.token != null)
		return this.state.token != null ? true : false;
	}

	render() {
		return (
			<AuthContext.Provider 
				value = {{
					token: this.state.token,
					signIn: this.signIn,
					signOut: this.signOut,
					isAuthenticated: this.isAuthenticated,
				}}
			>
				{this.props.children}
			</AuthContext.Provider>
		);
	}

}

export {AuthContextProvider, AuthContext};
