import 'react-native-gesture-handler';
import React from 'react';
import { 
	StyleSheet, 
	Text, 
	Button, 
	View,
	SafeAreaView, 
	TouchableOpacity 
} from 'react-native';

export default class JourneyScreen extends React.Component {
	render() {
		const { navigate } = this.props.navigation
		const { journey } = this.props.route.params
		return (
			<SafeAreaView style={styles.container}>
				<Text>
					{journey.name}
				</Text>
				<ExerciseCard
					navigation={this.props.navigation}
					exercise={journey.exercise[0]}
				/>
				<ExerciseCard
					navigation={this.props.navigation}
					exercise={journey.exercise[1]}
				/>
			</SafeAreaView>
		)
	}
}

class ExerciseCard extends React.Component {
	render() {
		const { navigate } = this.props.navigation
		const { exercise } = this.props
		console.log(this.props)
		return (
			<View style={styles.card}>
				<Text>Exercise: {exercise.name}</Text>
        <TouchableOpacity
					style={styles.journeyButton}
					onPress={() => {navigate("Exercise")}}
				>
          <Text>Begin Exercise</Text>
        </TouchableOpacity>
      </View>
		);
	}
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center'
  },
  view: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center'
	},
	card: {
		flex: 1,
		alignItems: 'center',
		justifyContent: 'center',
		borderColor: 'black',
		borderWidth: 1,
		borderRadius: 10,
		marginTop: 40,
		marginBottom: 40
	},
  journeyButton: {
    width: 200,
    marginRight: 40,
    marginLeft: 40,
    marginTop: 10,
    paddingTop: 10,
    paddingBottom: 10,
    backgroundColor: '#42A5F5',
    borderRadius: 10,
    borderWidth: 1,
    borderColor: '#fff',
    alignItems: 'center'
  }
})
